-- ----------------------------
-- Table structure for `t_blog`
-- ----------------------------
DROP TABLE IF EXISTS `t_blog`;
CREATE TABLE `t_blog` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `auth` int(11) NOT NULL,
  `category` int(11) NOT NULL,
  `content` text,
  `disSuggestTimes` int(11) NOT NULL,
  `enshrineTimes` int(11) NOT NULL,
  `lastEditDate` datetime DEFAULT NULL,
  `praisedTimes` int(11) NOT NULL,
  `publishDate` datetime DEFAULT NULL,
  `published` int(11) NOT NULL,
  `readedTimes` int(11) NOT NULL,
  `shredTimes` int(11) NOT NULL,
  `status` int(11) NOT NULL,
  `suggestedTimes` int(11) NOT NULL,
  `title` varchar(255) DEFAULT NULL,
  `user_id` bigint(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`),
  KEY `FKCB5B108D5A2B3E81` (`user_id`),
  CONSTRAINT `FKCB5B108D5A2B3E81` FOREIGN KEY (`user_id`) REFERENCES `t_user` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

